mapper.select:
select menu_id, menu_name, menu_href, menu_parent_id, menu_flag, menu_icon, create_date, update_date from menu where menu_flag = "Y"
and menu_parent_id = #{menuParentId}
and menu_name like concat("%",#{menuName},"$")
order by create_date desc
LIMIT ${(currentPage-1)*pageSize}, ${pageSize}

select count(1) from menu where menu_flag = "Y"
and menu_parent_id = #{menuParentId}
and menu_name like concat("%",#{menuName},"$")

delete.if:
delete from menu
where menu_id in

    #{item}

mapper.update:
UPDATE menu


    menu_name = #{menuName,jdbcType=VARCHAR},


    menu_href = #{menuHref,jdbcType=VARCHAR},


    menu_parent_id = #{menuParentId,jdbcType=VARCHAR},


    menu_flag = #{menuFlag,jdbcType=VARCHAR},


    menu_icon = #{menuIcon,jdbcType=VARCHAR}


    create_date = #{createDate,jdbcType=TIMESTAMP}


    update_date = #{updateDate,jdbcType=TIMESTAMP}


WHERE menu_id = #{menuId,jdbcType=VARCHAR}
